#include "httpd.h"
#include "http_config.h"
#include "http_protocol.h"
#include "ap_config.h"
#include "apr_global_mutex.h"
#include "http_log.h"

#define AP_SQ_MAIN_STATE            0
#define AP_SQ_MS_CREATE_PRE_CONFIG  2

int ap_state_query( int query_code );

typedef struct harbour_data {
    int iCounter;
    pthread_t pThreadMain;
} harbour_data;

apr_shm_t * harbour_shm;
apr_global_mutex_t * harbour_mutex;

static int harbour_handler( request_rec * r )
{
   apr_global_mutex_lock( harbour_mutex );
   ( ( harbour_data * ) apr_shm_baseaddr_get( harbour_shm ) )-&gt;iCounter++;
   apr_global_mutex_unlock(  harbour_mutex );

   if( ( ( harbour_data * ) apr_shm_baseaddr_get( harbour_shm ) )-&gt;pThreadMain == ( pthread_t ) NULL )
   {
      apr_global_mutex_lock( harbour_mutex );
      ( ( harbour_data * ) apr_shm_baseaddr_get( harbour_shm ) )-&gt;pThreadMain = pthread_self();
      apr_global_mutex_unlock(  harbour_mutex );
   }      

   if( ( ( harbour_data * ) apr_shm_baseaddr_get( harbour_shm ) )-&gt;pThreadMain == pthread_self() )
   {
      ap_rprintf( r, "counter main: %i", ( ( harbour_data * ) apr_shm_baseaddr_get( harbour_shm ) )-&gt;iCounter );
      apr_sleep( 200 );

      apr_global_mutex_lock( harbour_mutex );
      ( ( harbour_data * ) apr_shm_baseaddr_get( harbour_shm ) )-&gt;pThreadMain = ( pthread_t ) NULL;
      apr_global_mutex_unlock( harbour_mutex );   
   }   
   else
      ap_rprintf( r, "counter child: %i", ( ( harbour_data * ) apr_shm_baseaddr_get( harbour_shm ) )-&gt;iCounter );

   apr_global_mutex_lock( harbour_mutex );
   ( ( harbour_data * ) apr_shm_baseaddr_get( harbour_shm ) )-&gt;iCounter--;
   apr_global_mutex_unlock( harbour_mutex );

   return OK;
}

static apr_status_t shm_cleanup_wrapper( void * unused )
{
   unused = unused;
   return apr_shm_destroy( harbour_shm );
}

static int harbour_post_config( apr_pool_t * pconf, apr_pool_t * plog, apr_pool_t * ptemp, server_rec * s )
{
   plog = plog;
   ptemp = ptemp;
   s = s;

   if( ap_state_query( AP_SQ_MAIN_STATE ) == AP_SQ_MS_CREATE_PRE_CONFIG )
      return OK;  

   apr_shm_create( &amp;harbour_shm, sizeof( harbour_data ), NULL, pconf ); 

   ( ( harbour_data * ) apr_shm_baseaddr_get( harbour_shm ) )-&gt;iCounter = 0;    
   ( ( harbour_data * ) apr_shm_baseaddr_get( harbour_shm ) )-&gt;pThreadMain = ( pthread_t ) NULL;

   apr_pool_cleanup_register( pconf, NULL, shm_cleanup_wrapper, apr_pool_cleanup_null );

   return OK; 
}        

static void harbour_child_init( apr_pool_t * p, server_rec * s )
{
   s = s;
   if( APR_SUCCESS != apr_global_mutex_create( &amp;harbour_mutex, NULL, APR_LOCK_DEFAULT, p ) )
      exit( 1 );
}

static void harbour_register_hooks( apr_pool_t * p )
{
   p = p;
   ap_hook_post_config( harbour_post_config, NULL, NULL, APR_HOOK_MIDDLE);
   ap_hook_child_init( harbour_child_init, NULL, NULL, APR_HOOK_MIDDLE ); 
   ap_hook_handler( harbour_handler, NULL, NULL, APR_HOOK_MIDDLE );
}

/* Dispatch list for API hooks */
module AP_MODULE_DECLARE_DATA harbour_module = {
    STANDARD20_MODULE_STUFF,
    NULL,                  /* create per-dir    config structures */
    NULL,                  /* merge  per-dir    config structures */
    NULL,                  /* create per-server config structures */
    NULL,                  /* merge  per-server config structures */
    NULL,                  /* table of config file commands       */
    harbour_register_hooks  /* register hooks                      */
};

