&lt;html&gt;

	{{ View( 'head.view' ) }}

	&lt;?prg
		LOCAL hData 	:= PValue(1)
		LOCAL cHtml 	:= ''

		cHtml  += 	'&lt;div class="container"&gt;'        								+;
					'&lt;div class="row"&gt;'												+;
					'&lt;div class="col-md-10 col-lg-8"&gt;'  							+;
					'&lt;div class="post-preview"&gt;'									+;
					'&lt;a href="#"&gt;'													+;
						'&lt;h2 class="post-title"&gt;' + hData['titulo'] + '&lt;/h2&gt;'    	+;
						'&lt;h3 class="post-subtitle"&gt;' + hData['texto'] + '&lt;/h3&gt;'  	+;
					'&lt;/a&gt;'															+;
					'&lt;p class="post-meta"&gt;' + 'Posteado por&amp;nbsp;&lt;a href="#"&gt;' + 'usuario&lt;/a&gt;&lt;/p&gt;' +;
					'&lt;/div&gt;'	+;
					'&lt;hr&gt;'     	+;
					'&lt;div class="clearfix"&gt;&lt;button class="btn btn-primary float-right" type="button"&gt;regresars&amp;nbsp;⇒&lt;/button&gt;&lt;/div&gt;' +;
					'&lt;/div&gt;' +;
					'&lt;/div&gt;' +;
					'&lt;/div&gt;'


	    retu cHtml
	?&gt;

&lt;/html&gt;

&lt;head&gt;
    &lt;meta name="viewport" content="width=device-width, initial-scale=1.0, shrink-to-fit=no"&gt;
    &lt;title&gt;Blog ModHarbour&lt;/title&gt;
    &lt;link href="assets/bootstrap/css/bootstrap.min.css" rel="stylesheet"&gt;

    &lt;link href="<https://fonts.googleapis.com/css?family=Open+Sans:300italic,400italic,600italic,700italic,800italic,400,300,600,700,800>" rel="stylesheet"&gt;&lt;meta charset="utf-8"&gt;
    &lt;link href="<https://fonts.googleapis.com/css?family=Lora:400,700,400italic,700italic>" rel="stylesheet"&gt;
    &lt;link href="assets/fonts/font-awesome.min.css" rel="stylesheet"&gt;

    &lt;header class="masthead" style="background-image:url('/home-bg.jpg');"&gt;
        &lt;div class="overlay"&gt;&lt;/div&gt;
        &lt;div class="container"&gt;
            &lt;div class="row"&gt;
                &lt;div class="col-md-10 col-lg-8 mx-auto"&gt;&lt;div class="site-heading"&gt;
    &lt;h1&gt;Mod-Harbour Blog&lt;/h1&gt;&lt;span class="subheading"&gt;Nuestro Blog !!&lt;/span&gt;&lt;/div&gt;&lt;/div&gt;
            &lt;/div&gt;
        &lt;/div&gt;
    &lt;/header&gt;

&lt;/head&gt;

&lt;html&gt;
	&lt;meta charset="UTF-8"/&gt;
	{{ View( 'head.view' ) }}

	&lt;?prg
		LOCAL hData 	:= PValue(1)
		LOCAL cHtml 	:= ''
		LOCAL nI

		cHtml +=  	'&lt;div class="container"&gt;' 	+;
        			'&lt;div class="row"&gt;'			+;
            		'&lt;div class="col-md-10 col-lg-8"&gt;'


		FOR nI := 1 TO len( hData[ 'rows' ] )
			cHtml += ;
			'&lt;div class="post-preview"&gt;'									+;
			'&lt;a href="#"&gt;'													+;
				'&lt;h2 class="post-title"&gt;' + hData[ 'rows'][ nI ]['titulo'] + '&lt;/h2&gt;'    	+;
				'&lt;h3 class="post-subtitle"&gt;' + hData[ 'rows'][ nI ]['texto'] + '&lt;/h3&gt;'  	+;
			'&lt;/a&gt;'															+;
			'&lt;p class="post-meta"&gt;' + 'Posteado por&amp;nbsp;&lt;a href="#"&gt;' + 'usuario&lt;/a&gt;&lt;/p&gt;' +;
			'&lt;/div&gt;'	

			cHtml += '&lt;form action="{{ Route( 'read' ) }}" method="GET"&gt;'
			cHtml += '	&lt;button type="submit" name="recno" value="' + ltrim(str( hData[ 'rows'][ nI ][ '_recno'] )) + '"&gt;Read&lt;/button&gt;'
			cHtml += '&lt;/form&gt;'
			cHtml += '&lt;hr&gt;'

		NEXT
		cHtml+= 	'&lt;/div&gt;' +;
					'&lt;/div&gt;' +;
					'&lt;/div&gt;'


	    retu cHtml
	?&gt;

	&lt;br&gt;Last Recno = {{ PARAM 1, 'recno' }}

	&lt;form action="{{ Route( 'root' ) }}" method="POST"&gt;
		&lt;input type="hidden" name="recno" value="{{ PARAM 1, 'recno' }}"&gt;		
		&lt;input type="submit" name="action" value="prev" &gt;
		&lt;input type="submit" name="action" value="Next" &gt;
		&lt;input type="submit" name="action" value="gotop" &gt;
		&lt;input type="submit" name="action" value="gobottom" &gt;
		&lt;input type="submit" name="action" value="buscar" &gt;
		&lt;input type="search" id="buscar" name="search"&gt;
		&lt;input type="submit" name="action" value="limpiar" &gt;

	&lt;/form&gt;
&lt;/html&gt;

&lt;form action="{{ Route( 'read' ) }}" method="GET"&gt;
		&lt;input type="submit" name="action" value="Home" &gt;
	&lt;/form&gt;
