function mask($text)
{
  $b1 = 0x80 | (0x1 &amp; 0x0f);
  $length = strlen($text);

  if($length &lt;= 125)
    $header = pack('CC', $b1, $length);
  elseif($length &gt; 125 &amp;&amp; $length &lt; 65536)
    $header = pack('CCn', $b1, 126, $length);
  elseif($length &gt;= 65536)
    $header = pack('CCNN', $b1, 127, $length);
  return $header.$text;
}

