   cVData  :=  STRTRAN(cVData, chr(246), "&amp;ouml;"   )
   cVData  :=  STRTRAN(cVData, chr(148), "&amp;ouml;"   )

   cVData  :=  STRTRAN(cVData, chr(252), "&amp;uuml;"   )
