function Main()

    local aColumnas
    local i
    local cTitle := 'IFT rotaciones'

	set date french
	set century on
	set delete on


    use ( hb_GetEnv( "PRGPATH" ) + '/rotacion' ) new alias data shared
    aColumnas = {}
    for i = 1 to fcount()
        aadd( aColumnas, alltrim( fieldname( i ) ) )
    next

    data-&gt;( dbclosearea() )

	TEMPLATE PARAMS aColumnas, cTitle
		<!DOCTYPE html>
		&lt;html lang="en"&gt;
		&lt;head&gt;
			&lt;meta charset="UTF-8"&gt;
			&lt;title&gt;&lt;?prg return cTitle ?&gt;&lt;/title&gt;
			&lt;script src="<https://ajax.googleapis.com/ajax/libs/jquery/3.4.1/jquery.min.js>"&gt;&lt;/script&gt;
			&lt;script src="<https://cdn.datatables.net/1.10.19/js/jquery.dataTables.min.js>"&gt;&lt;/script&gt;
			&lt;script src="<https://cdn.datatables.net/1.10.19/js/dataTables.bootstrap4.min.js>"&gt;&lt;/script&gt;
			&lt;link rel="stylesheet" type="text/css" href="<https://cdnjs.cloudflare.com/ajax/libs/twitter-bootstrap/4.1.3/css/bootstrap.css>"/&gt;
			&lt;link rel="stylesheet" type="text/css" href="<https://cdn.datatables.net/1.10.19/css/dataTables.bootstrap4.min.css>"/&gt;
		&lt;/head&gt;
		&lt;body&gt;
		&lt;div class="container"&gt;
  			&lt;div class="row"&gt;
  			   &lt;h1&gt;&lt;?prg return cTitle ?&gt;&lt;/h1&gt;
    			&lt;div class="col-sm-12"&gt;
					&lt;table id="example" class="table table-striped table-bordered" style="width:100%"&gt;
						&lt;thead&gt;
                            &lt;?prg
                            local i
                            local cHtml := ''

                            cHtml += '&lt;tr&gt;'
                            for i = 1 to len( aColumnas )
                                cHtml += '&lt;th&gt;' + aColumnas[ i ] + '&lt;/th&gt;'
                            next
                            cHtml += '&lt;/tr&gt;'
                            return cHtml
						    ?&gt;
						&lt;/thead&gt;
					&lt;/table&gt;
				&lt;div&gt;
			&lt;/div&gt;
		&lt;/div&gt;
			&lt;script&gt;

				$(document).ready( function () {

		    		$('#example').DataTable({
						"ajax": "rotacionjs.prg",
						"scrollCollapse": true,
						"paging":         true,
						"columns": [
						&lt;?prg
						    local i
						    local cHt := ''
			                    for i = to len( aColumnas )
			                        if i = len( aColumnas )
			                            cHt += '{ "data":"' + aColumnas[i] + '"}'
			                        else
						                cHt += '{ "data":"' + aColumnas[i] + '"},'
						            end
						        next
						    return cHt
						?&gt;
        				]
		    		});
				});

			&lt;/script&gt;
		&lt;/body&gt;
		&lt;/html&gt;
	ENDTEXT

return nil
