`#xtranslate HTML &lt;c&gt; =&gt; #pragma __text| &lt;c&gt;+=%s`

#xtranslate HTML &lt;c&gt; [ PARAMS [&lt;v1&gt;] [,&lt;vn&gt;] ] ;
=&gt; ;
	#pragma __cstream |&lt;c&gt;+= InlinePrg( ReplaceBlocks( %s, '&lt;$', "$&gt;" [,&lt;(v1)&gt;][+","+&lt;(vn)&gt;] [, @&lt;v1&gt;][, @&lt;vn&gt;] ) )

function main()
	Local cHtml:= ''
	local aDat := { 'pera', 'manzana', 'platano', 'cereza', 'melocoton' }
	local n

	HTML cHtml
      &lt;html&gt;
         &lt;head&gt;
	ENDTEXT

   cHtml += '&lt;b&gt;Inicio líneas de texto&lt;/b&gt;'

   for n := 1 To 5

		HTML cHtml PARAMS n, aDat

			&lt;div style="border:1px solid"&gt;

				My Header...

				&lt;table border="1"&gt;
					&lt;tr&gt;
						&lt;td&gt;&lt;$ n $&gt;&lt;/td&gt;
						&lt;td&gt;&lt;$ aDat[n] $&gt;&lt;/td&gt;
					&lt;/tr&gt;
				&lt;/table&gt;

			&lt;/div&gt;			

		ENDTEXT	  

    next

    HTML cHtml
		  &lt;div style="border:1px solid red"&gt;Footer&lt;div&gt;
        &lt;head/&gt;
      &lt;html/&gt;
    ENDTEXT

    AP_RPuts(cHtml)

retu nil

