function busqueda( cBus )

    local cAlias := alias ()
    local hData  := {=&gt;}

    hData['arows'] = {}

    select product
    nReg = product-&gt;( recno() )
    dbgotop()
    locate for lower( alltrim( cBus ) ) $  lower( product-&gt;descrip )
    while found()

        aadd( hData[ 'arows' ], { '_recno'  =&gt; product-&gt;( recno() ) , ;
                                  'codprod' =&gt; product-&gt;codprod     , ;
                                  'descrp'  =&gt; product-&gt;descrip     , ;
                                  'img1'    =&gt; product-&gt;img1 } )
        continue
    end

    hData['found'] = if ( len( hData['arows'] ) &gt; 0, .t., .f. )

    select product
    dbgoto( nReg ) //dejo donde estaba

    if !empty( cAlias )
        select ( cAlias )
    end

