// Arc es necesario para compartirlo entre threads, y
// un clone de Arc simplemente te da dos "nombres" al mismo puntero
let mutex = Arc::new(Mutex::new(0));
let c_mutex = mutex.clone();

// .unwrap y .expect son parte del mecanismo de errores de Rust,
// hacen terminar el programa si el lock no se ha podido hacer o
// si no se ha podido crear el thread
