function Main( aH )

	TEMPLATE PARAMS aH
		&lt;!DOCTYPE html&gt;
		&lt;html lang="en"&gt;
		&lt;head&gt;
			&lt;meta charset="UTF-8"&gt;
			&lt;title&gt;Test Datatable&lt;/title&gt;
			&lt;script src="<https://ajax.googleapis.com/ajax/libs/jquery/3.4.1/jquery.min.js>"&gt;&lt;/script&gt;
			&lt;script src="<https://cdn.datatables.net/1.10.19/js/jquery.dataTables.min.js>"&gt;&lt;/script&gt;
			&lt;script src="<https://cdn.datatables.net/1.10.19/js/dataTables.bootstrap4.min.js>"&gt;&lt;/script&gt;
			&lt;link rel="stylesheet" type="text/css" href="<https://cdnjs.cloudflare.com/ajax/libs/twitter-bootstrap/4.1.3/css/bootstrap.css>"/&gt;
			&lt;link rel="stylesheet" type="text/css" href="<https://cdn.datatables.net/1.10.19/css/dataTables.bootstrap4.min.css>"/&gt;
		&lt;/head&gt;
		&lt;body&gt;
		&lt;div class="container"&gt;
  			&lt;div class="row"&gt;
  			   &lt;h1&gt;Test DataTable&lt;/h1&gt;
    			&lt;div class="col-sm-12"&gt;
					&lt;table id="example" class="table table-striped table-bordered" style="width:100%"&gt;
                  &lt;?prg return HeaderTable( aH ) ?&gt;
					&lt;/table&gt;
				&lt;div&gt;
			&lt;/div&gt;
		&lt;/div&gt;
			&lt;script&gt;

				$(document).ready( function () {

		    		$('#example').DataTable({

						"ajax": "datajson.prg",
						"scrollCollapse": true,
						"paging":         true,
						"columns": [
                  &lt;?prg return DatasTable() ?&gt;
        				]
		    		});
				});

			&lt;/script&gt;
		&lt;/body&gt;
		&lt;/html&gt;
	ENDTEXT

return nil

//----------------------------------------------------------------------------//

Function HeaderTable( aHeaders )

   local cStr := ""
   local x
   hb_default( @aHeaders, { "ID", "First", "Last", "Street", "City", "State", "Zip", ;
                       "Hiredate", "Married", "Age", "Salary" } )
   cStr    += "&lt;thead&gt;" + CRLF
   cStr    += "&lt;tr&gt;" + CRLF
   For x = 1 to Len( aHeaders )
      cStr += "&lt;th&gt;" + aHeaders[ x ] + "&lt;/th&gt;" + CRLF
   Next x
   cStr    += "&lt;/tr&gt;" + CRLF
   cStr    += "&lt;/thead&gt;" + CRLF

Return cStr

//----------------------------------------------------------------------------//

Function DatasTable( aDatos )

   local cStr   := ""
   local x
   hb_default( @aDatos, { "id", "first", "last", "street", "city", "state", "zip", ;
                       "hiredate", "married", "age", "salary" } )
   For x = 1 to Len( aDatos )
      cStr   += '{ "data": "' + aDatos[ x ] + '" }' + if( x &lt; Len( aDatos ), ",", "" ) + CRLF
   Next x
   //? cStr

Return cStr

//----------------------------------------------------------------------------//

&lt;script type="text/javascript" src="jquery.dataTables.js"&gt;&lt;/script&gt;
&lt;script type="text/javascript"&gt;
    $(document).ready(function() {
        $('#example').dataTable( {
            "language": {
                "url": "dataTables.german.lang"
            }
        } );
    } );
&lt;/script&gt;
