#include "<http://FiveWin.ch|FiveWin.ch>"

static oWnd, oFont
static cResultPath := ""
//----------------------------------------------------------------------------//

function Main( cDbfFile )
	local aFiles := {}
	local oBtn
   *----------------------------------------------------------
	DEFINE FONT oFont ;
          NAME "Segoe UI" ;
          SIZE 0, 22


	IF cDbfFile&lt;&gt;nil
		AADD( aFiles, cDbfFile )
	endif

   DEFINE WINDOW oWnd TITLE "Droping Files from FileManager to change path from local to web"

          @ 50,10 ;
   BUTTONBMP oBtn ;
          OF oWnd ;
       PIXEL ;
           SIZE 200, 56 ;
      PROMPT "Domain Path" + CRLF +  "to Clipboard" ;
        FONT oFont ;
      ACTION ( CopyToClipboard( "EX" ), oWnd:End() )


 	    @ 50,250 ;
   BUTTONBMP  ;
          OF oWnd ;
             PIXEL ;
        SIZE 200, 56 ;
      PROMPT "LOCALHOST-&gt;Path" + CRLF +  "to Clipboard" ;
        FONT oFont ;
      ACTION ( CopyToClipboard( "LH" ), oWnd:End() )


      @ 200,5 ;
   BUTTONBMP  ;
          OF oWnd ;
        SIZE 100, 32 ;
       PIXEL ;
      PROMPT "setup" ;
        FONT oFont ;
      ACTION setup()





   oWnd:bInit := {||IIF( cDbfFile&lt;&gt;nil, ( PaintTheName( 0, 0, afiles  ) ), ), oWnd:refresh() }

   ACTIVATE WINDOW oWnd ;
      ON DROPFILES PaintTheName( nRow, nCol, aFiles )

return nil

//----------------------------------------------------------------------------//

function PaintTheName( nRow, nCol, aFiles )

   local cName, cResult := ""
   local n := 1
   while ! Empty( cName := StrToken( aFiles[ 1 ], n++, "\" ) )
      if "~" $ cName
         cName = SFN2LFN( cResult + cName )
      endif
      cResult += cName + "\"
   end

   cResult = SubStr( cResult, 1, Len( cResult ) - 1 )

   oWnd:Say( nRow, nCol, cResult,,,, .t. )  // Say in pixels
   cResultPath :=  cResult

return nil
//----------------------------------------------------------------------------//

function CopyToClipboard( cCall )
	local cText			:= lower( cResultPath )
   local oClip 		:= TClipBoard():New()
	local cDomain 		:= GetPvProfString( "Setup", "domainname", "<https://winhotel.space/>",  ".\test2web.ini")
	local clocalDir 	:= GetPvProfString( "Setup", "localDir", "c:\www\htdocs\",  ".\test2web.ini")
	*----------------------------------------------------------

	WritePProString( "Setup", "CopyToClipboard", cCall,".\test2web.ini"  )

	if cCall = "LH"
		cText := STRTRAN( cText, clocalDir, "localhost/"  )
	else
		fwlog cDomain , clocalDir

 		cText := STRTRAN( cText, clocalDir, cDomain  )

	endif

	cText := STRTRAN(cText, "\", "/"  )

   if oClip:Open()
      oClip:SetText( cText )
      oClip:Close()
   endif

   oClip:End()

return nil

//----------------------------------------------------------------------------//



