function AP_PostPairs()

   local cPair, uPair, hPairs := {=&gt;}

   for each cPair in hb_ATokens( AP_Body(), "&amp;" )
      if ( uPair := At( "=", cPair ) ) &gt; 0
            hb_HSet( hPairs, Left( cPair, uPair - 1 ), SubStr( cPair, uPair + 1 ) )
      endif
    next

return hPairs

